    static var sampleSplitNow: ChartData {
        var data = ChartData()
        let date = Date()
        let time = Double(date.timeIntervalSince1970)
        let split = ChartData.Split(date: Int(time), numerator: 3, denominator: 1, splitRatio: "3:1")
        let event = ChartData.Events(dividends: nil, splits: ["\(time)": split])
        data.events = event
        return data
    }
    
    static var sampleDividendNow: ChartData {
        var data = ChartData()
        let date = Date()
        let time = Double(date.timeIntervalSince1970)
        let dividend = ChartData.Dividend(amount: 1, date: Int(time))
        let event = ChartData.Events(dividends: ["\(time)": dividend], splits: nil)
        data.events = event
        return data
    }
    
    
